    def dequeue(self):
        if not self.is_empty():
            while len(stack1) > 0:
                stack2.append(stack1.pop())
            result = stack2.pop()
            while len(stack2) > 0:
                stack1.append(stack2.pop())
            return result

    # Check whether the queue is empty or not
    def is_empty(self):
        return len(stack1) == 0 and len(stack2) == 0

    # Return the size of the queue
    def size(self):
        return len(stack1) + len(stack2)
