class Solution {
    public boolean canPartition(int[] nums, int target) {
        int n = nums.length;

        //initialize dp table
        Boolean[][] dp = new Boolean[n+1][target + 1];

        //base condition 
        for( int i = 0; i < n ;i++){
            dp[i][0] = true;
        }

        //fill remaining cells
        for(int i=n;i>=0 && i<=n;i--){
             for(int j=target;j>=0;j--) {
                dp[i][j]=dp[i-1][j];  
                if(j >= nums[i-1]) {  
                    dp[i][j] |= dp[i-1][j-nums[i-1]];  
                }   
           }
       }

     return dp[n][target];
}
