public boolean isPowerOfThree(int n) {
    // Base cases (n < 0 or n > 0 but not equal to any power of 3)
    if(n<=0){
        return false;
    }

    while(n>9){     // Keep dividing by largest powers of 10 until we are left with only 1, 3 or 9
        int temp=n/3; // Divide by 3
        if(temp==n){   // If result is same as before division then n was already a power of 3
